mapper.update:
UPDATE users
SET
    user_name = #{userName}

    ,password = #{password}

    ,addr = #{addr}
    ,m_number = #{phone}
    ,s_number = #{companyPhone}
    ,company_name = #{companyName}
WHERE user_srl = #{userSrl}

UPDATE users
SET
    master_srl = #{userSrl}
WHERE user_srl = #{userSrl}
