mapper.select:
SELECT o.obj_srl AS objSrl, c.company_srl AS companySrl, c.company_name AS companyName, c.addr AS companyAddr, c.phone AS companyPhone, c.latitude, c.longitude, o.obj_status AS objStatus FROM obj AS o INNER JOIN company AS c ON o.company_srl = c.company_srl INNER JOIN users AS u ON c.user_srl = u.user_srl
WHERE u.master_srl = #{userSrl}
WHERE u.user_srl = #{userSrl}

mapper.update:
UPDATE users
SET
    user_name = #{userName}

    ,password = #{password}

    ,addr = #{addr}
    ,detail_addr = #{detailAddr}
    ,m_number = #{phone}
    ,s_number = #{companyPhone}
    ,company_name = #{companyName}
WHERE user_srl = #{userSrl}

UPDATE users
SET
    master_srl = #{userSrl}
WHERE user_srl = #{userSrl}
