mapper.select:
SELECT u.auth, o.qr_srl AS qrSrl, o.obj_img AS objImage, o.obj_status AS objStatus, DATE_FORMAT(o.obj_status_at,'%Y.%m.%d, %H시%i분') AS statusAt FROM obj AS o INNER JOIN users AS u ON o.user_srl = u.user_srl WHERE o.user_srl = #{userSrl}
AND o.obj_status IN ('inner_wait','return_finish', 'return_wait')
AND o.obj_status IN ('outer_wait', 'release_finish', 'return_start', 'return_wait','release_start')
ORDER BY o.create_at DESC LIMIT #{limitcount},#{contentnum}

SELECT count(*) FROM obj WHERE user_srl = #{userSrl}
AND obj_status IN ('inner_wait','return_finish', 'return_wait')
AND obj_status IN ('outer_wait', 'release_finish', 'return_start','return_wait','release_start')

mapper.update:
UPDATE users
SET
    user_name = #{userName},
    m_number = #{phone}
WHERE user_srl = #{userSrl}
