mapper.update:
UPDATE users
SET
    user_name = #{userName},
    m_number = #{phone}
WHERE user_srl = #{userSrl}

mapper.select:
SELECT o.obj_status AS objStatus, o.obj_m AS modelName, o.obj_img_mn AS modelImageName, IFNULL(o.obj_kinds,'미정') AS objKinds, u.user_name AS userName, o.qr_srl AS qrSrl FROM obj AS o INNER JOIN users AS u ON o.user_srl = u.user_srl WHERE u.master_srl = #{userSrl}
AND o.obj_kinds IS NULL
AND o.obj_kinds = '레이저 복합기'
AND o.obj_kinds = '토너 카트리지'
ORDER BY u.user_name ASC
LIMIT #{limitcount},#{contentnum}
