mapper.select:
SELECT obj_srl AS objSrl, obj_status AS objStatus, DATE_FORMAT(obj_status_at,'%Y.%m.%d') AS statusAt, obj_m AS modelName, obj_kinds AS objKinds, qr_srl AS qrSrl, u.user_name AS userName, c.company_name AS companyName FROM obj AS o INNER JOIN users AS u ON o.user_srl = u.user_srl INNER JOIN company AS c ON o.company_srl = c.company_srl WHERE u.master_srl = #{userSrl}
AND obj_status IN ('return_finish','inner_wait')
AND obj_status IN ('release_finish','outer_wait')
AND obj_status = #{objStatus}
ORDER BY obj_status_at DESC LIMIT #{limitcount},#{contentnum}

SELECT count(*) FROM obj AS o INNER JOIN users AS u ON o.user_srl = u.user_srl WHERE u.master_srl = #{userSrl}
AND obj_status IN ('return_finish','inner_wait')
AND obj_status IN ('release_finish','outer_wait')
AND obj_status = #{objStatus}
