mapper.select:
SELECT obj_srl AS objSrl, obj_status AS objStatus, DATE_FORMAT(obj_status_at,'%Y.%m.%d') AS statusAt, obj_m AS modelName, obj_img_mn AS modelImageName, obj_s AS srlName, obj_img_srl AS srlImageName, IFNULL(obj_kinds,'미정') AS objKinds, qr_srl AS qrSrl, u.user_name AS userName, c.company_name AS companyName FROM obj AS o INNER JOIN users AS u ON o.user_srl = u.user_srl INNER JOIN company AS c ON o.company_srl = c.company_srl WHERE u.master_srl = #{userSrl}
AND obj_status IN ('warehousing','warehousing_new')
AND obj_status IN ('release','release_new')
AND obj_status = #{objStatus}
AND obj_status = #{objStatus}
ORDER BY obj_status_at DESC LIMIT #{limitcount},#{contentnum}

SELECT
    count(*)
FROM obj AS o
INNER JOIN users AS u
ON o.user_srl = u.user_srl
WHERE u.master_srl = #{userSrl}

    AND obj_status IN ('warehousing','warehousing_new')


    AND obj_status IN ('release','release_new')


    AND obj_status = #{objStatus}


    AND obj_status = #{objStatus}

mapper.update:
UPDATE obj
SET
    obj_m = #{modelName},
    obj_s = #{srlName},
    obj_kinds = #{objKinds}
WHERE qr_srl = #{qrSrl}

UPDATE company
SET
company_name = #{companyName},
addr = #{companyAddr},
detail_addr = #{companyDetailAddr},
phone = #{companyPhone}

,latitude = #{latitude}


 ,longitude = #{longitude}

WHERE company_srl = #{companySrl}
